// src/components/FocusView.jsx

import { useState, useEffect, useRef } from 'react';

const formatTimer = (seconds) => {
    const m = Math.floor(seconds / 60).toString().padStart(2, '0');
    const s = (seconds % 60).toString().padStart(2, '0');
    return `${m}:${s}`;
};

const FocusView = ({ session, onSessionEnd, onStop }) => {
    const [timeLeft, setTimeLeft] = useState(session.duration);
    const [isPaused, setIsPaused] = useState(false);
    const timerRef = useRef(null);
    const audioRef = useRef(null);
    
    // Create notification sound
    useEffect(() => {
        // Create audio context for notification sound
        audioRef.current = new Audio();
        // You can use a data URL for a simple beep sound or add an audio file

        return () => {
            if (timerRef.current) {
                clearInterval(timerRef.current);
            }
        };
    }, []);

    // Timer effect - khởi tạo và quản lý timer
    useEffect(() => {
        if (!isPaused && timeLeft > 0) {
            timerRef.current = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);
        } else {
            if (timerRef.current) {
                clearInterval(timerRef.current);
            }
        }

        return () => {
            if (timerRef.current) {
                clearInterval(timerRef.current);
            }
        };
    }, [isPaused, timeLeft]);

    // Effect để xử lý khi timer kết thúc
    useEffect(() => {
        if (timeLeft <= 0) {
            if (timerRef.current) {
                clearInterval(timerRef.current);
            }
            // Khi kết thúc, trả về thời gian đã làm việc (toàn bộ thời gian của session)
            onSessionEnd(session.duration); 
        }
    }, [timeLeft, onSessionEnd, session.duration]);
    
    const progress = ((session.duration - timeLeft) / session.duration) * 100;

    return (
        <div className="flex flex-col items-center justify-center w-full h-full bg-slate-900 text-white p-6">
            <div className="w-full h-1 bg-slate-700 fixed top-0 left-0">
                <div className="h-1 bg-blue-500" style={{ width: `${progress}%` }}></div>
            </div>
            
            <p className="text-xl font-semibold text-slate-300 mb-4 text-center">
                {session.task.name}
            </p>
            
            <h2 className="text-8xl font-extrabold tracking-tighter">
                {formatTimer(timeLeft)}
            </h2>

            <div className="fixed bottom-10 flex space-x-6">
                <button onClick={() => setIsPaused(!isPaused)} className="text-slate-400 font-semibold py-3 px-6 rounded-lg bg-slate-800">
                    {isPaused ? '▶️ Tiếp tục' : '⏸️ Tạm dừng'}
                </button>
                <button onClick={onStop} className="text-slate-400 font-semibold py-3 px-6 rounded-lg bg-slate-800">
                    ⏹️ Dừng
                </button>
            </div>
        </div>
    );
};

export default FocusView;
