// src/App.jsx

import { useState, useEffect, useMemo } from "react";
import Header from "./components/Header";
import TaskList from "./components/TaskList";
import FocusView from "./components/FocusView";
import HistoryView from "./components/HistoryView";
import {
  TaskModal,
  SessionEndModal,
  ConfirmStopModal,
  ConfirmDeleteModal,
  DailyTargetModal,
  LoadingModal,
  ErrorModal,
} from "./components/Modals";
import databaseService from "./services/databaseService";

const App = () => {
  // State management
  const [tasks, setTasks] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [dailyTarget, setDailyTarget] = useState(0);
  const [activeSession, setActiveSession] = useState(null);
  const [modal, setModal] = useState(null);
  const [taskToStart, setTaskToStart] = useState(null);
  const [taskToDelete, setTaskToDelete] = useState(null);
  const [filter, setFilter] = useState("day");
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  // Load initial data
  useEffect(() => {
    const loadInitialData = async () => {
      setLoading(true);
      try {
        const [tasksData, sessionsData, targetData] = await Promise.all([
          databaseService.getTasks(),
          databaseService.getSessions(),
          databaseService.getDailyTarget(),
        ]);

        setTasks(tasksData);
        setSessions(sessionsData);
        setDailyTarget(targetData.target_minutes * 60); // Convert to seconds
      } catch (error) {
        console.error('Error loading initial data:', error);
        setError('KhÃ´ng thá»ƒ táº£i dá»¯ liá»‡u. Sá»­ dá»¥ng dá»¯ liá»‡u offline.');

        // Fallback to localStorage
        const localTasks = databaseService.getLocalTasks();
        const localSessions = databaseService.getLocalSessions();

        setTasks(localTasks);
        setSessions(localSessions);
      } finally {
        setLoading(false);
      }
    };

    loadInitialData();
  }, []);

  // Listen for online/offline events
  useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true);
      setError(null);
      // Refresh data when coming back online
      databaseService.syncLocalData();
    };

    const handleOffline = () => {
      setIsOnline(false);
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const filteredSessions = useMemo(() => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    return sessions.filter((session) => {
      const sessionDate = new Date(session.completed_at);
      switch (filter) {
        case "week":
          const oneWeekAgo = new Date(today).setDate(today.getDate() - 7);
          return sessionDate >= oneWeekAgo;
        case "month":
          return (
            sessionDate.getMonth() === now.getMonth() &&
            sessionDate.getFullYear() === now.getFullYear()
          );
        case "day":
        default:
          return sessionDate.setHours(0, 0, 0, 0) === today.getTime();
      }
    });
  }, [sessions, filter]);

  const todayFocusTime = useMemo(() => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    const todaySessions = sessions.filter((session) => {
      const sessionDate = new Date(session.completed_at);
      return sessionDate.setHours(0, 0, 0, 0) === today.getTime();
    });

    return todaySessions.reduce((acc, session) => acc + session.duration, 0);
  }, [sessions]);

  // Task management functions
  const addTask = async (taskName) => {
    setLoading(true);
    setError(null);

    try {
      const newTask = await databaseService.addTask(taskName);
      setTasks(prev => [...prev, newTask]);
      return newTask;
    } catch (error) {
      console.error('Error adding task:', error);
      setError('KhÃ´ng thá»ƒ thÃªm task má»›i');
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const deleteTask = async (taskId) => {
    setLoading(true);
    setError(null);

    try {
      await databaseService.deleteTask(taskId);
      setTasks(prev => prev.filter(task => task.id !== taskId));
      setSessions(prev => prev.filter(session => session.task_id !== taskId));
      return true;
    } catch (error) {
      console.error('Error deleting task:', error);
      setError('KhÃ´ng thá»ƒ xÃ³a task');
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const addSession = async (taskId, duration) => {
    setLoading(true);
    setError(null);

    try {
      const newSession = await databaseService.addSession(taskId, duration);
      setSessions(prev => [...prev, newSession]);
      return newSession;
    } catch (error) {
      console.error('Error adding session:', error);
      setError('KhÃ´ng thá»ƒ lÆ°u phiÃªn lÃ m viá»‡c');
      throw error;
    } finally {
      setLoading(false);
    }
  };

  // Event handlers
  const handleAddTask = async (taskName) => {
    try {
      await addTask(taskName);
      setModal(null);
    } catch (error) {
      setModal("error");
    }
  };

  const handleStartSession = (task, duration) => {
    const session = {
      task: task,
      duration: duration === 0 ? 0 : duration * 60,
    };
    setActiveSession(session);
    setModal(null);
  };

  const handleAddSession = async (timeWorked) => {
    if (!activeSession) return;

    try {
      await addSession(activeSession.task.id, timeWorked);
      setActiveSession(null);
      setModal(null);
    } catch (error) {
      setModal("error");
    }
  };

  const handleStopSession = () => {
    setActiveSession(null);
    setModal(null);
  };

  const handleDeleteTask = (task) => {
    setTaskToDelete(task);
    setModal("confirmDelete");
  };

  const handleConfirmDelete = async () => {
    if (!taskToDelete) return;

    try {
      await deleteTask(taskToDelete.id);
      setModal(null);
      setTaskToDelete(null);
    } catch (error) {
      setModal("error");
    }
  };

  const handleCancelDelete = () => {
    setModal(null);
    setTaskToDelete(null);
  };

  const handleSetDailyTarget = async (targetMinutes) => {
    setLoading(true);
    try {
      await databaseService.setDailyTarget(targetMinutes);
      setDailyTarget(targetMinutes * 60);
      setModal(null);
    } catch (error) {
      console.error('Error setting daily target:', error);
      setError('KhÃ´ng thá»ƒ lÆ°u má»¥c tiÃªu hÃ ng ngÃ y');
    } finally {
      setLoading(false);
    }
  };

  const handleErrorClose = () => {
    setModal(null);
    setError(null);
  };

  const handleRetry = async () => {
    setError(null);
    setModal(null);

    // Try to refresh data
    try {
      setLoading(true);
      const [tasksData, sessionsData] = await Promise.all([
        databaseService.getTasks(),
        databaseService.getSessions(),
      ]);

      setTasks(tasksData);
      setSessions(sessionsData);
    } catch (error) {
      console.error('Retry failed:', error);
      setError('Váº«n khÃ´ng thá»ƒ káº¿t ná»‘i. Sá»­ dá»¥ng cháº¿ Ä‘á»™ offline.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="h-screen w-screen bg-slate-100 text-slate-800 antialiased overflow-hidden flex flex-col">
      {/* Connection Status Indicator */}
      {!isOnline && (
        <div className="bg-yellow-500 text-white text-center py-1 text-sm font-medium">
          ðŸš« Offline - Dá»¯ liá»‡u sáº½ Ä‘Æ°á»£c Ä‘á»“ng bá»™ khi cÃ³ káº¿t ná»‘i
        </div>
      )}

      {!activeSession ? (
        <>
          <Header
            sessions={filteredSessions}
            filter={filter}
            dailyTarget={dailyTarget}
            todayFocusTime={todayFocusTime}
            onSetTarget={() => setModal("dailyTarget")}
          />

          <div className="flex-grow overflow-y-auto px-4 pb-24">
            <HistoryView
              sessions={filteredSessions}
              tasks={tasks}
              filter={filter}
              setFilter={setFilter}
            />
            <TaskList
              tasks={tasks}
              onTaskClick={(task) => {
                setTaskToStart(task);
                setModal("startTask");
              }}
              onTaskDelete={handleDeleteTask}
            />
          </div>

          <button
            onClick={() => setModal("addTask")}
            className="fixed bottom-6 right-6 bg-blue-600 text-white rounded-full w-14 h-14 flex items-center justify-center shadow-lg hover:bg-blue-700 transition transform hover:scale-110 z-20"
            aria-label="ThÃªm task má»›i"
            disabled={loading}
          >
            <svg
              className="w-7 h-7"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M12 6v6m0 0v6m0-6h6m-6 0H6"
              ></path>
            </svg>
          </button>
        </>
      ) : (
        <FocusView
          session={activeSession}
          onSessionEnd={handleAddSession}
          onStop={() => setModal("confirmStop")}
        />
      )}

      {/* Loading Modal */}
      {loading && <LoadingModal />}

      {/* Error Modal */}
      {modal === "error" && (
        <ErrorModal
          error={error}
          onClose={handleErrorClose}
          onRetry={handleRetry}
        />
      )}

      {(modal === "addTask" || modal === "startTask") && (
        <TaskModal
          task={taskToStart}
          onClose={() => {
            setModal(null);
            setTaskToStart(null);
          }}
          onStartSession={handleStartSession}
          onAddTask={handleAddTask}
        />
      )}

      {modal === "dailyTarget" && (
        <DailyTargetModal
          currentTarget={Math.round(dailyTarget / 60)}
          onClose={() => setModal(null)}
          onSetTarget={handleSetDailyTarget}
        />
      )}

      {modal === "sessionEnd" && (
        <SessionEndModal
          onComplete={() => handleAddSession(activeSession.duration)}
          onPause={handleStopSession}
        />
      )}

      {modal === "confirmStop" && (
        <ConfirmStopModal
          onConfirm={handleStopSession}
          onCancel={() => setModal(null)}
        />
      )}

      {modal === "confirmDelete" && (
        <ConfirmDeleteModal
          task={taskToDelete}
          onConfirm={handleConfirmDelete}
          onCancel={handleCancelDelete}
        />
      )}
    </div>
  );
};

export default App;
