import React from 'react';

const formatTime = (seconds) => {
    if (!seconds || seconds < 60) return `${seconds || 0}s`;
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    let str = '';
    if (h > 0) str += `${h}h`;
    if (m > 0) str += ` ${m}p`;
    return str.trim();
};

const Header = ({ tasks }) => {
    const now = new Date();
    const completedTasks = tasks.filter(t => t.status === 'completed').length;
    const totalFocusSeconds = tasks.reduce((acc, task) => acc + (task.totalTime || 0), 0);

    return (
        <header className="p-4 pt-6">
            <h1 className="text-2xl font-bold text-slate-900">
                Hôm nay - {now.toLocaleDateString('vi-VN', { weekday: 'long', day: 'numeric', month: 'numeric' })}
            </h1>
            <p className="text-sm text-slate-500 font-medium mt-1">
                ✅ {completedTasks} task hoàn thành • ⏱️ {formatTime(totalFocusSeconds)} tập trung
            </p>
        </header>
    );
};

export default Header;
