// src/components/Header.jsx

import React, { useState, useEffect } from 'react';
import fileStorageService from '../services/fileStorageService';

// H√†m ƒë·ªãnh d·∫°ng th·ªùi gian t·ª´ gi√¢y sang gi·ªù, ph√∫t
const formatTime = (seconds) => {
  if (!seconds || seconds < 60) return `${seconds || 0} gi√¢y`;
  const h = Math.floor(seconds / 3600);
  const m = Math.floor((seconds % 3600) / 60);
  let str = '';
  if (h > 0) str += `${h} gi·ªù `;
  if (m > 0) str += `${m} ph√∫t`;
  return str.trim();
};

// H√†m l·∫•y vƒÉn b·∫£n t∆∞∆°ng ·ª©ng v·ªõi b·ªô l·ªçc
const getFilterText = (filter) => {
  if (filter === 'day') return 'h√¥m nay';
  if (filter === 'week') return 'tu·∫ßn n√†y';
  if (filter === 'month') return 'th√°ng n√†y';
  return '';
};

const Header = ({ sessions, filter, dailyTarget, todayFocusTime, onSetTarget }) => {
  const [fileStatus, setFileStatus] = useState(null);

  useEffect(() => {
    // Get initial file status
    setFileStatus(fileStorageService.getFileStatus());

    // Update file status every minute
    const interval = setInterval(() => {
      setFileStatus(fileStorageService.getFileStatus());
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  // T√≠nh to√°n t·ªïng th·ªùi gian t·∫≠p trung t·ª´ sessions ƒë∆∞·ª£c filter
  const totalFocusSeconds = sessions.reduce((acc, session) => acc + session.duration, 0);
  
  // T√≠nh progress n·∫øu c√≥ target (ch·ªâ cho filter 'day')
  const progress = (filter === 'day' && dailyTarget > 0) 
    ? Math.min((todayFocusTime / dailyTarget) * 100, 100) 
    : 0;
  
  const isTargetCompleted = filter === 'day' && dailyTarget > 0 && todayFocusTime >= dailyTarget;

  return (
    <header className="bg-white shadow-sm border-b px-4 py-6">
      <div className="max-w-md mx-auto">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-slate-800 flex items-center justify-center">
            DeepWork
            {isTargetCompleted && filter === 'day' && (
              <span className="ml-2 text-2xl animate-bounce">üéâ</span>
            )}
          </h1>
          <p className="text-sm text-slate-500 font-medium mt-1">
            ‚è±Ô∏è ƒê√£ t·∫≠p trung ƒë∆∞·ª£c {formatTime(totalFocusSeconds)} {getFilterText(filter)} [ {new Date().toLocaleDateString('vi-VN')} ]
          </p>
          
          {/* File Status Indicator */}
          {fileStatus && (
            <div className="mt-1 flex items-center justify-center space-x-2">
              <span className={`text-xs ${
                fileStatus.hasFileHandle 
                  ? 'text-green-600' 
                  : fileStatus.isFileAPISupported 
                    ? 'text-yellow-600' 
                    : 'text-blue-600'
              }`}>
                {fileStatus.hasFileHandle ? 'üìÅ ƒê√£ k·∫øt n·ªëi file' : 
                 fileStatus.isFileAPISupported ? 'üìÑ LocalStorage + File API' : 
                 'üíæ LocalStorage backup'}
              </span>
            </div>
          )}
          
          {/* Daily Target Section - Only show on 'day' filter */}
          {filter === 'day' && (
            <div className="mt-3">
              {dailyTarget > 0 ? (
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-xs text-slate-600 font-medium">
                      M·ª•c ti√™u h√¥m nay: {formatTime(dailyTarget)}
                    </span>
                    <button 
                      onClick={onSetTarget}
                      className="text-xs text-blue-600 hover:text-blue-700 font-medium"
                    >
                      Thay ƒë·ªïi
                    </button>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className={`h-2 rounded-full transition-all duration-500 ${
                        isTargetCompleted ? 'bg-green-500' : 'bg-blue-500'
                      }`}
                      style={{ width: `${Math.min(progress, 100)}%` }}
                    ></div>
                  </div>
                  <div className="flex items-center justify-between text-xs">
                    <span className="text-slate-600">
                      {formatTime(todayFocusTime)} / {formatTime(dailyTarget)}
                    </span>
                    <span className={`font-bold ${isTargetCompleted ? 'text-green-600' : 'text-blue-600'}`}>
                      {Math.round(progress)}%
                    </span>
                  </div>
                  {isTargetCompleted && (
                    <div className="bg-green-50 border border-green-200 rounded-lg p-2 mt-2">
                      <span className="text-xs text-green-700 font-medium flex items-center justify-center">
                        üéØ Ch√∫c m·ª´ng! B·∫°n ƒë√£ ho√†n th√†nh m·ª•c ti√™u h√¥m nay!
                      </span>
                    </div>
                  )}
                </div>
              ) : (
                <button 
                  onClick={onSetTarget}
                  className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center"
                >
                  üéØ ƒê·∫∑t m·ª•c ti√™u cho h√¥m nay
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
