// src/components/GoogleAuth.js
import React, { useState, useEffect } from 'react';
import googleAuthService from '../services/googleAuthService';

const GoogleAuth = ({ onAuthSuccess }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if user is already authenticated
    setIsAuthenticated(googleAuthService.isAuthenticated());

    // Handle OAuth callback
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    
    if (code) {
      handleAuthCallback(code);
    }
  }, []);

  const handleAuthCallback = async (code) => {
    setLoading(true);
    setError(null);
    
    try {
      await googleAuthService.exchangeCodeForTokens(code);
      setIsAuthenticated(true);
      
      // Clear URL parameters
      window.history.replaceState({}, document.title, window.location.pathname);
      
      if (onAuthSuccess) {
        onAuthSuccess();
      }
    } catch (err) {
      setError('Xác thực thất bại. Vui lòng thử lại.');
      console.error('Authentication error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSignIn = () => {
    setLoading(true);
    const authUrl = googleAuthService.getAuthUrl();
    window.location.href = authUrl;
  };

  const handleSignOut = () => {
    googleAuthService.signOut();
    setIsAuthenticated(false);
    if (onAuthSuccess) {
      onAuthSuccess();
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        <span className="ml-3">Đang xác thực...</span>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold text-center mb-4">Đăng nhập Google</h2>
        <p className="text-gray-600 text-center mb-6">
          Đăng nhập để đồng bộ dữ liệu với Google Sheets
        </p>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}
        
        <button
          onClick={handleSignIn}
          className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center"
        >
          <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
            <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
            <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
            <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
            <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
          </svg>
          Đăng nhập với Google
        </button>
      </div>
    );
  }

  return (
    <div className="flex items-center justify-between p-4 bg-green-100 rounded-lg mb-4">
      <div className="flex items-center">
        <div className="w-3 h-3 bg-green-500 rounded-full mr-3"></div>
        <span className="text-green-800 font-medium">Đã kết nối Google Sheets</span>
      </div>
      <button
        onClick={handleSignOut}
        className="text-red-600 hover:text-red-800 text-sm font-medium"
      >
        Đăng xuất
      </button>
    </div>
  );
};

export default GoogleAuth;
