import React from 'react';
import { formatTime, getFilterText } from '../utils/formatters';
import DailyProgress from './DailyProgress';

// Icon component (b·∫°n c√≥ th·ªÉ d√πng th∆∞ vi·ªán nh∆∞ heroicons/react)
const Icon = ({ path, className = "w-6 h-6" }) => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className={className}>
    <path strokeLinecap="round" strokeLinejoin="round" d={path} />
  </svg>
);

const Header = ({ sessions, filter, dailyTarget, todayFocusTime, onSetTarget }) => {
  const totalFocusSeconds = sessions.reduce((acc, session) => acc + session.duration, 0);
  const isTargetCompleted = filter === 'day' && dailyTarget > 0 && todayFocusTime >= dailyTarget;

  return (
    <header className="bg-slate-50/80 backdrop-blur-sm p-4 sm:p-6 border-b border-slate-200">
      <div className="max-w-5xl mx-auto">
        {/* D√≤ng ti√™u ƒë·ªÅ ch√≠nh */}
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
            <div className="bg-indigo-600 p-2 rounded-lg shadow">
              <Icon path="M12 6.042A8.967 8.967 0 006 3.75c-1.052 0-2.062.18-3 .512v14.25A8.987 8.987 0 016 18c2.305 0 4.408.867 6 2.292m0-14.25a8.966 8.966 0 016-2.292c1.052 0 2.062.18 3 .512v14.25A8.987 8.987 0 0018 18a8.967 8.967 0 00-6 2.292m0-14.25v14.25" className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-2xl sm:text-3xl font-bold text-slate-800">
              DeepWork Focus
              {isTargetCompleted && (
                <span className="ml-2 text-2xl animate-bounce">üéâ</span>
              )}
            </h1>
          </div>
          <div className="text-right">
            <p className="text-sm text-slate-600 font-medium">
              {new Date().toLocaleDateString('vi-VN', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
            </p>
          </div>
        </div>

        {/* L∆∞·ªõi th·ªëng k√™ */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Card th·ªëng k√™ chung */}
          <div className="bg-white/60 p-4 rounded-lg border border-slate-200">
            <h3 className="font-semibold text-slate-700 text-base mb-2">
              Th·ªëng k√™ nhanh
            </h3>
            <div className="flex items-end gap-2">
              <p className="text-4xl font-bold text-slate-800 leading-none">
                {formatTime(totalFocusSeconds) || "0 ph√∫t"}
              </p>
              <p className="text-slate-500 text-sm mb-1">
                t·ªïng th·ªùi gian t·∫≠p trung {getFilterText(filter)}
              </p>
            </div>
          </div>

          {/* Card m·ª•c ti√™u h√†ng ng√†y */}
          {filter === 'day' && (
            <DailyProgress
              dailyTarget={dailyTarget}
              todayFocusTime={todayFocusTime}
              onSetTarget={onSetTarget}
            />
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
