// src/components/Header.jsx

import React, { useState, useEffect } from 'react';
import fileStorageService from '../services/fileStorageService';

// Hàm định dạng thời gian từ giây sang giờ, phút
const formatTime = (seconds) => {
  if (!seconds || seconds < 60) return `${seconds || 0} giây`;
  const h = Math.floor(seconds / 3600);
  const m = Math.floor((seconds % 3600) / 60);
  let str = '';
  if (h > 0) str += `${h} giờ `;
  if (m > 0) str += `${m} phút`;
  return str.trim();
};

// Hàm lấy văn bản tương ứng với bộ lọc
const getFilterText = (filter) => {
  if (filter === 'day') return 'hôm nay';
  if (filter === 'week') return 'tuần này';
  if (filter === 'month') return 'tháng này';
  return '';
};

const Header = ({ sessions, filter, dailyTarget, todayFocusTime, onSetTarget }) => {
  const [fileStatus, setFileStatus] = useState(null);

  useEffect(() => {
    // Get initial file status
    setFileStatus(fileStorageService.getFileStatus());

    // Update file status every minute
    const interval = setInterval(() => {
      setFileStatus(fileStorageService.getFileStatus());
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  // Tính toán tổng thời gian tập trung từ sessions được filter
  const totalFocusSeconds = sessions.reduce((acc, session) => acc + session.duration, 0);
  
  // Tính progress nếu có target (chỉ cho filter 'day')
  const progress = (filter === 'day' && dailyTarget > 0) 
    ? Math.min((todayFocusTime / dailyTarget) * 100, 100) 
    : 0;
  
  const isTargetCompleted = filter === 'day' && dailyTarget > 0 && todayFocusTime >= dailyTarget;

  return (
    <header className="bg-white shadow-sm border-b px-4 py-4">
      <div className="max-w-4xl mx-auto">
        {/* Header Title - Left aligned */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-3">
            <h1 className="text-2xl font-bold text-slate-800">
              DeepWork
              {isTargetCompleted && filter === 'day' && (
                <span className="ml-2 text-xl animate-bounce">🎉</span>
              )}
            </h1>
            
            {/* File Status Indicator */}
            {fileStatus && (
              <span className={`text-xs px-2 py-1 rounded-full ${
                fileStatus.hasFileHandle 
                  ? 'bg-green-100 text-green-700' 
                  : fileStatus.isFileAPISupported 
                    ? 'bg-yellow-100 text-yellow-700' 
                    : 'bg-blue-100 text-blue-700'
              }`}>
                {fileStatus.hasFileHandle ? '📁' : 
                 fileStatus.isFileAPISupported ? '📄' : 
                 '💾'}
              </span>
            )}
          </div>
          
          <div className="text-right">
            <p className="text-sm text-slate-500 font-medium">
              {new Date().toLocaleDateString('vi-VN')}
            </p>
          </div>
        </div>
        
        {/* Stats and Target - Left aligned */}
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <p className="text-sm text-slate-600">
              ⏱️ Đã tập trung được <span className="font-semibold text-slate-800">{formatTime(totalFocusSeconds)}</span> {getFilterText(filter)}
            </p>
          </div>
          
          {/* Daily Target Section - Only show on 'day' filter */}
          {filter === 'day' && (
            <div className="space-y-2">
              {dailyTarget > 0 ? (
                <>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-slate-600">
                      🎯 Mục tiêu hôm nay: <span className="font-semibold">{formatTime(dailyTarget)}</span>
                    </span>
                    <button 
                      onClick={onSetTarget}
                      className="text-xs text-blue-600 hover:text-blue-700 font-medium px-2 py-1 rounded hover:bg-blue-50 transition-colors"
                    >
                      Thay đổi
                    </button>
                  </div>
                  
                  <div className="flex items-center space-x-3">
                    <div className="flex-1 bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full transition-all duration-500 ${
                          isTargetCompleted ? 'bg-green-500' : 'bg-blue-500'
                        }`}
                        style={{ width: `${Math.min(progress, 100)}%` }}
                      ></div>
                    </div>
                    <span className={`text-sm font-bold min-w-fit ${
                      isTargetCompleted ? 'text-green-600' : 'text-blue-600'
                    }`}>
                      {Math.round(progress)}%
                    </span>
                  </div>
                  
                  <div className="flex items-center justify-between text-xs text-slate-500">
                    <span>
                      {formatTime(todayFocusTime)} / {formatTime(dailyTarget)}
                    </span>
                    {isTargetCompleted && (
                      <span className="text-green-600 font-medium">
                        ✅ Hoàn thành mục tiêu!
                      </span>
                    )}
                  </div>
                </>
              ) : (
                <button 
                  onClick={onSetTarget}
                  className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center space-x-1 px-2 py-1 rounded hover:bg-blue-50 transition-colors"
                >
                  <span>🎯</span>
                  <span>Đặt mục tiêu cho hôm nay</span>
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
