// src/App.jsx

import { useState, useEffect, useMemo } from "react";
import Header from "./components/Header";
import TaskList from "./components/TaskList";
import FocusView from "./components/FocusView";
import HistoryView from "./components/HistoryView";
import {
  TaskModal,
  SessionEndModal,
  ConfirmStopModal,
  ConfirmDeleteModal,
  DailyTargetModal,
  LoadingModal,
  ErrorModal,
} from "./components/Modals";
import { FileManagerModal } from "./components/FileManagerModal";
import fileStorageService from "./services/fileStorageService";

const App = () => {
  // State management
  const [tasks, setTasks] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [dailyTarget, setDailyTarget] = useState(0);
  const [activeSession, setActiveSession] = useState(null);
  const [modal, setModal] = useState(null);
  const [taskToStart, setTaskToStart] = useState(null);
  const [taskToDelete, setTaskToDelete] = useState(null);
  const [filter, setFilter] = useState("day");

  // Load initial data
  useEffect(() => {
    const loadInitialData = async () => {
      setLoading(true);
      try {
        const [tasksData, sessionsData, targetData] = await Promise.all([
          fileStorageService.getTasks(),
          fileStorageService.getSessions(),
          fileStorageService.getDailyTarget(),
        ]);

        setTasks(tasksData);
        setSessions(sessionsData);
        setDailyTarget(targetData.targetMinutes * 60); // Convert to seconds

        console.log('Data loaded successfully:', {
          tasks: tasksData.length,
          sessions: sessionsData.length,
          target: targetData.targetMinutes
        });
      } catch (error) {
        console.error('Error loading initial data:', error);
        setError('Không thể tải dữ liệu: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    loadInitialData();

    // Cleanup on unmount
    return () => {
      fileStorageService.cleanup();
    };
  }, []);

  // Auto-refresh data when file changes (for manual sync)
  useEffect(() => {
    const refreshInterval = setInterval(async () => {
      try {
        const [tasksData, sessionsData] = await Promise.all([
          fileStorageService.getTasks(),
          fileStorageService.getSessions(),
        ]);

        // Only update if data actually changed
        if (JSON.stringify(tasksData) !== JSON.stringify(tasks)) {
          setTasks(tasksData);
        }
        if (JSON.stringify(sessionsData) !== JSON.stringify(sessions)) {
          setSessions(sessionsData);
        }
      } catch (error) {
        console.warn('Error refreshing data:', error);
      }
    }, 60000); // Check every minute

    return () => clearInterval(refreshInterval);
  }, [tasks, sessions]);

  const filteredSessions = useMemo(() => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    return sessions.filter((session) => {
      const sessionDate = new Date(session.completedAt);
      switch (filter) {
        case "week":
          const oneWeekAgo = new Date(today).setDate(today.getDate() - 7);
          return sessionDate >= oneWeekAgo;
        case "month":
          return (
            sessionDate.getMonth() === now.getMonth() &&
            sessionDate.getFullYear() === now.getFullYear()
          );
        case "day":
        default:
          return sessionDate.setHours(0, 0, 0, 0) === today.getTime();
      }
    });
  }, [sessions, filter]);

  const todayFocusTime = useMemo(() => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    const todaySessions = sessions.filter((session) => {
      const sessionDate = new Date(session.completedAt);
      return sessionDate.setHours(0, 0, 0, 0) === today.getTime();
    });

    return todaySessions.reduce((acc, session) => acc + session.duration, 0);
  }, [sessions]);

  // Task management functions
  const addTask = async (taskName) => {
    setLoading(true);
    setError(null);

    try {
      const newTask = await fileStorageService.addTask(taskName);
      setTasks(prev => [...prev, newTask]);
      return newTask;
    } catch (error) {
      console.error('Error adding task:', error);
      setError('Không thể thêm task mới: ' + error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const deleteTask = async (taskId) => {
    setLoading(true);
    setError(null);

    try {
      await fileStorageService.deleteTask(taskId);
      setTasks(prev => prev.filter(task => task.id !== taskId));
      setSessions(prev => prev.filter(session => session.taskId !== taskId));
      return true;
    } catch (error) {
      console.error('Error deleting task:', error);
      setError('Không thể xóa task: ' + error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const addSession = async (taskId, duration) => {
    setLoading(true);
    setError(null);

    try {
      const newSession = await fileStorageService.addSession(taskId, duration);
      setSessions(prev => [...prev, newSession]);
      return newSession;
    } catch (error) {
      console.error('Error adding session:', error);
      setError('Không thể lưu phiên làm việc: ' + error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  // Add this function to get all daily targets
  const getAllDailyTargets = async () => {
    try {
      // Get current month's targets
      const now = new Date();
      const targets = {};

      // Get targets for current month (simplified - you may want to optimize this)
      for (let day = 1; day <= 31; day++) {
        const date = `${now.getFullYear()}-${(now.getMonth() + 1).toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
        try {
          const target = await fileStorageService.getDailyTarget(date);
          if (target.targetMinutes > 0) {
            targets[date] = target;
          }
        } catch (error) {
          // Skip if error
        }
      }

      return targets;
    } catch (error) {
      console.error('Error getting daily targets:', error);
      return {};
    }
  };

  // Update the component to include dailyTargets
  const [dailyTargetsMap, setDailyTargetsMap] = useState({});

  // Load daily targets
  useEffect(() => {
    const loadDailyTargets = async () => {
      try {
        const targets = await getAllDailyTargets();
        setDailyTargetsMap(targets);
      } catch (error) {
        console.error('Error loading daily targets:', error);
      }
    };

    loadDailyTargets();
  }, []);

  // Event handlers
  const handleAddTask = async (taskName) => {
    try {
      await addTask(taskName);
      setModal(null);
    } catch (error) {
      setModal("error");
    }
  };

  const handleStartSession = (task, duration) => {
    const session = {
      task: task,
      duration: duration === 0 ? 0 : duration * 60,
    };
    setActiveSession(session);
    setModal(null);
  };

  const handleAddSession = async (timeWorked) => {
    if (!activeSession) return;

    try {
      await addSession(activeSession.task.id, timeWorked);
      setActiveSession(null);
      setModal(null);
    } catch (error) {
      setModal("error");
    }
  };

  const handleStopSession = () => {
    setActiveSession(null);
    setModal(null);
  };

  const handleDeleteTask = (task) => {
    setTaskToDelete(task);
    setModal("confirmDelete");
  };

  const handleConfirmDelete = async () => {
    if (!taskToDelete) return;

    try {
      await deleteTask(taskToDelete.id);
      setModal(null);
      setTaskToDelete(null);
    } catch (error) {
      setModal("error");
    }
  };

  const handleCancelDelete = () => {
    setModal(null);
    setTaskToDelete(null);
  };

  const handleSetDailyTarget = async (targetMinutes) => {
    setLoading(true);
    try {
      await fileStorageService.setDailyTarget(targetMinutes);
      setDailyTarget(targetMinutes * 60);
      setModal(null);
    } catch (error) {
      console.error('Error setting daily target:', error);
      setError('Không thể lưu mục tiêu hàng ngày: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleErrorClose = () => {
    setModal(null);
    setError(null);
  };

  const handleRetry = async () => {
    setError(null);
    setModal(null);

    // Try to reload data
    try {
      setLoading(true);
      const [tasksData, sessionsData] = await Promise.all([
        fileStorageService.getTasks(),
        fileStorageService.getSessions(),
      ]);

      setTasks(tasksData);
      setSessions(sessionsData);
    } catch (error) {
      console.error('Retry failed:', error);
      setError('Vẫn không thể tải dữ liệu: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleFileManagerSuccess = async () => {
    // Reload data after file operations
    try {
      const [tasksData, sessionsData] = await Promise.all([
        fileStorageService.getTasks(),
        fileStorageService.getSessions(),
      ]);

      setTasks(tasksData);
      setSessions(sessionsData);
    } catch (error) {
      console.error('Error reloading after file operation:', error);
    }
  };

  return (
    <div className="h-screen w-screen bg-slate-100 text-slate-800 antialiased overflow-hidden flex flex-col">
      {!activeSession ? (
        <>
          <Header
            sessions={filteredSessions}
            filter={filter}
            dailyTarget={dailyTarget}
            todayFocusTime={todayFocusTime}
            onSetTarget={() => setModal("dailyTarget")}
          />

          <div className="flex-grow overflow-y-auto px-4 pb-24">
            <HistoryView
              sessions={filteredSessions}
              tasks={tasks}
              filter={filter}
              setFilter={setFilter}
              dailyTargets={dailyTargetsMap}
            />
            <TaskList
              tasks={tasks}
              onTaskClick={(task) => {
                setTaskToStart(task);
                setModal("startTask");
              }}
              onTaskDelete={handleDeleteTask}
            />
          </div>

          {/* Floating Action Buttons */}
          <div className="fixed bottom-6 right-6 flex flex-col space-y-3 z-20">
            {/* File Manager Button */}
            <button
              onClick={() => setModal("fileManager")}
              className="bg-green-600 text-white rounded-full w-12 h-12 flex items-center justify-center shadow-lg hover:bg-green-700 transition transform hover:scale-110"
              aria-label="Quản lý file dữ liệu"
              title="Quản lý file dữ liệu"
            >
              📁
            </button>

            {/* Add Task Button */}
            <button
              onClick={() => setModal("addTask")}
              className="bg-blue-600 text-white rounded-full w-14 h-14 flex items-center justify-center shadow-lg hover:bg-blue-700 transition transform hover:scale-110"
              aria-label="Thêm task mới"
              disabled={loading}
            >
              <svg
                className="w-7 h-7"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M12 6v6m0 0v6m0-6h6m-6 0H6"
                ></path>
              </svg>
            </button>
          </div>
        </>
      ) : (
        <FocusView
          session={activeSession}
          onSessionEnd={handleAddSession}
          onStop={() => setModal("confirmStop")}
        />
      )}

      {/* Loading Modal */}
      {loading && <LoadingModal />}

      {/* Error Modal */}
      {modal === "error" && (
        <ErrorModal
          error={error}
          onClose={handleErrorClose}
          onRetry={handleRetry}
        />
      )}

      {/* File Manager Modal */}
      {modal === "fileManager" && (
        <FileManagerModal
          onClose={() => setModal(null)}
          onSuccess={handleFileManagerSuccess}
        />
      )}

      {(modal === "addTask" || modal === "startTask") && (
        <TaskModal
          task={taskToStart}
          onClose={() => {
            setModal(null);
            setTaskToStart(null);
          }}
          onStartSession={handleStartSession}
          onAddTask={handleAddTask}
        />
      )}

      {modal === "dailyTarget" && (
        <DailyTargetModal
          currentTarget={Math.round(dailyTarget / 60)}
          onClose={() => setModal(null)}
          onSetTarget={handleSetDailyTarget}
        />
      )}

      {modal === "sessionEnd" && (
        <SessionEndModal
          onComplete={() => handleAddSession(activeSession.duration)}
          onPause={handleStopSession}
        />
      )}

      {modal === "confirmStop" && (
        <ConfirmStopModal
          onConfirm={handleStopSession}
          onCancel={() => setModal(null)}
        />
      )}

      {modal === "confirmDelete" && (
        <ConfirmDeleteModal
          task={taskToDelete}
          onConfirm={handleConfirmDelete}
          onCancel={handleCancelDelete}
        />
      )}
    </div>
  );
};

export default App;
