// src/components/DailyProgress.jsx

import React from 'react';
import { formatTime } from '../utils/formatters';

// Icon component (bạn có thể dùng thư viện như heroicons/react)
const Icon = ({ path, className = "w-5 h-5" }) => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className={className}>
    <path strokeLinecap="round" strokeLinejoin="round" d={path} />
  </svg>
);

const DailyProgress = ({ dailyTarget, todayFocusTime, onSetTarget }) => {
  // Nếu chưa có mục tiêu, hiển thị nút để thiết lập
  if (!dailyTarget || dailyTarget <= 0) {
    return (
      <div className="bg-white/60 p-4 rounded-lg border border-slate-200 flex flex-col items-center justify-center text-center h-full">
        <Icon path="M9 12.75L11.25 15 15 9.75M21 12a9 9 0 11-18 0 9 9 0 0118 0z" className="w-12 h-12 text-slate-400 mb-2" />
        <h3 className="font-semibold text-slate-700">Chưa có mục tiêu</h3>
        <p className="text-sm text-slate-500 mb-3">Hãy đặt mục tiêu để bắt đầu ngày làm việc hiệu quả!</p>
        <button
          onClick={onSetTarget}
          className="flex items-center gap-2 text-sm bg-indigo-600 text-white font-semibold px-4 py-2 rounded-lg hover:bg-indigo-700 transition-colors shadow-sm"
        >
          <Icon path="M12 4.5v15m7.5-7.5h-15" className="w-4 h-4" />
          Đặt mục tiêu ngay
        </button>
      </div>
    );
  }

  // Tính toán tiến độ
  const progress = Math.min((todayFocusTime / dailyTarget) * 100, 100);
  const isTargetCompleted = todayFocusTime >= dailyTarget;

  return (
    <div className="bg-white/60 p-4 rounded-lg border border-slate-200 h-full">
      <div className="flex justify-between items-center mb-1">
        <h3 className="font-semibold text-slate-700 text-base">
          🎯 Mục tiêu hôm nay
        </h3>
        <button
          onClick={onSetTarget}
          className="text-xs text-indigo-600 hover:text-indigo-800 font-medium transition-colors"
        >
          Thay đổi
        </button>
      </div>
      <p className="text-xs text-slate-500 mb-3">
        {formatTime(todayFocusTime)} / <span className="font-medium">{formatTime(dailyTarget)}</span>
      </p>

      {/* Progress Bar */}
      <div className="flex items-center gap-3">
        <div className="flex-1 bg-slate-200 rounded-full h-2.5">
          <div
            className={`h-2.5 rounded-full transition-all duration-500 ${isTargetCompleted ? 'bg-green-500' : 'bg-indigo-500'}`}
            style={{ width: `${progress}%` }}
          ></div>
        </div>
        <span className={`text-sm font-bold min-w-[45px] text-right ${isTargetCompleted ? 'text-green-600' : 'text-indigo-600'}`}>
          {Math.round(progress)}%
        </span>
      </div>
      
      {isTargetCompleted && (
        <p className="text-xs text-green-600 font-medium mt-2 animate-pulse">
          🎉 Chúc mừng bạn đã hoàn thành mục tiêu!
        </p>
      )}
    </div>
  );
};

export default DailyProgress;
