// src/components/FocusView.jsx

import { useState, useEffect, useRef } from 'react';

const formatTimer = (seconds) => {
    const m = Math.floor(seconds / 60).toString().padStart(2, '0');
    const s = (seconds % 60).toString().padStart(2, '0');
    return `${m}:${s}`;
};

// Create notification sound player using HTML5 Audio
const createNotificationSound = () => {
    try {
        // Function to play notification sound multiple times
        const playNotificationSound = async (times = 3, interval = 500) => {
            for (let i = 0; i < times; i++) {
                try {
                    const audio = new Audio('/noti.mp3');
                    audio.volume = 0.7; // Set volume to 70%
                    
                    // Create promise to wait for audio to finish
                    const playPromise = new Promise((resolve, reject) => {
                        audio.onended = resolve;
                        audio.onerror = reject;
                        audio.oncanplaythrough = () => {
                            audio.play().catch(reject);
                        };
                    });
                    
                    // Load and play audio
                    audio.load();
                    await playPromise;
                    
                    // Wait before playing next sound (except for last iteration)
                    if (i < times - 1) {
                        await new Promise(resolve => setTimeout(resolve, interval));
                    }
                } catch (error) {
                    console.warn(`Failed to play notification sound ${i + 1}:`, error);
                    // Continue with next iteration even if one fails
                }
            }
        };
        
        return playNotificationSound;
    } catch (error) {
        console.log('Audio not supported:', error);
        return () => Promise.resolve(); // Return empty async function if audio not supported
    }
};

// Tab focus management utility
const TabFocusManager = {
    // Store original title to restore later
    originalTitle: document.title,
    
    // Focus tab with visual indicators
    focusTab: () => {
        try {
            // Change document title to grab attention
            document.title = 'üéâ DeepWork - Phi√™n ho√†n th√†nh!';
            
