                      onChange={(e) => setName(e.target.value)}
                      placeholder="‚úèÔ∏è Nh·∫≠p t√™n c√¥ng vi·ªác..." 
                      className="w-full text-lg font-semibold border-none focus:ring-0 p-2 mb-3" 
                      required 
                      disabled={!!task}
