// src/components/TaskItem.jsx

import React from 'react';

const TaskItem = ({ task, onClick, onDelete }) => (
  <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow">
      <div className="flex justify-between items-center">
          <div className="flex-grow">
              <h3 className="font-semibold text-lg text-slate-800 mb-1">{task.name}</h3>
              {task.description && (
                <p className="text-sm text-slate-600 mb-2">{task.description}</p>
              )}
              <div className="flex items-center space-x-4 text-xs text-slate-500">
                <span>T·∫°o l√∫c: {new Date(task.created_at).toLocaleString('vi-VN')}</span>
                <span>Sessions: {task.session_count || 0}</span>
                <span>T·ªïng: {Math.round((task.total_time_seconds || 0) / 60)}p</span>
                {task.is_local && (
                  <span className="px-2 py-1 bg-yellow-100 text-yellow-800 rounded-full text-xs">
                    Offline
                  </span>
                )}
              </div>
          </div>
          <div className="flex items-center space-x-2">
              <button 
                  onClick={(e) => {
                      e.stopPropagation();
                      onDelete(task);
                  }}
                  className="text-red-500 hover:text-red-700 transition-colors p-1"
                  aria-label="X√≥a task"
              >
                  üóëÔ∏è
              </button>
              <span 
                  onClick={onClick}
                  className="bg-blue-100 text-blue-700 font-bold py-2 px-4 rounded-lg cursor-pointer hover:bg-blue-200 transition-colors"
              >
                  ‚ñ∂Ô∏è B·∫Øt ƒë·∫ßu
              </span>
          </div>
      </div>
  </div>
);

export default TaskItem;
